import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';
import downloadImage from '../../assets/download.jpg';

function Admin() {
  const [employees, setEmployees] = useState([]);
  const [view, setView] = useState('home'); 
  const [username, setUsername] = useState('Admin'); 
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('/api/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error("Failed to fetch employees", error);
      }
    };
    
    if (view === 'employeeList') {
      fetchEmployees();
    }
  }, [view]);

  const handleLogout = () => {
    navigate('/');
  };

  return (
    <div>
    <div className="containerr">
      <h2>Admin Dashboard</h2>
      <div className='nav'>
        <div className="nav-buttons">
          <button onClick={() => setView('home')}>Home</button>
          <button onClick={() => setView('employeeList')}>Employee List</button>
          <button onClick={() => navigate('/create-employee')}>Create Employee</button>
        </div>
        <div className='butt'>
            <span>{username}</span>
            <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
      </div>

      {/* Conditional Rendering Based on View */}
      <div className="content">
        {view === 'home' && <p>Welcome to the Admin Dashboard</p>}
        
        {view === 'employeeList' && (
          <>
            <h3>Employee List</h3>
            <ul>
              {employees.map((employee) => (
                <li key={employee.id}>
                  <strong>{employee.name}</strong> - {employee.position}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>

      {/* Footer with Username and Logout Button */}
      <div className="footer">
        
        
      </div>
    </div>
  );
}

export default Admin;
