import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';
import downloadImage from '../../assets/download.jpg';

function Admin() {
  const [employees, setEmployees] = useState([]);
  const [view, setView] = useState('home'); 
  const [username, setUsername] = useState('Admin'); 
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('/api/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error("Failed to fetch employees", error);
      }
    };
    
    if (view === 'employeeList') {
      fetchEmployees();
    }
  }, [view]);

  const handleLogout = () => {
    navigate('/');
  };

  return (
    <div>
      <img src={downloadImage} alt='admin'/>
      <div className="containerr">
        <h2>Admin Dashboard</h2>
        <div className='nav'>
          <div className="nav-buttons">
            <button onClick={() => setView('home')}>Home</button>
            <button onClick={() => setView('employeeList')}>Employee List</button>
            <button onClick={() => setView('create-emp') & navigate('/create-employee')}>Create Employee</button>
          </div>
          <div className='butt'>
              <span>{username}</span>
              <button onClick={handleLogout}>Logout</button>
          </div>
        </div>
        
        <div className="content">
          {view === 'home' && <h3>Welcome to the Admin Dashboard</h3>}
          
          {view === 'employeeList' && (
            <>
              <h3>Employee List</h3>
              <ul>
                {employees.map((employee) => (
                  <li key={employee.id}>
                    <strong>{employee.name}</strong> - {employee.position}
                  </li>
                ))}
              </ul>
            </>
          )}
          {view === ''}
        </div>
        </div>
      </div>
  );
}

export default Admin;
